import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { MapContainer, TileLayer, Marker, Popup, Polyline } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// Fix Leaflet marker icon path
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
  iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
});

const responder = {
  latitude: 14.3160,
  longitude: 121.0797,
};

function haversineDistance([lat1, lon1], [lat2, lon2]) {
  const R = 6371;
  const dLat = ((lat2 - lat1) * Math.PI) / 180;
  const dLon = ((lon2 - lon1) * Math.PI) / 180;
  const a =
    Math.sin(dLat / 2) ** 2 +
    Math.cos((lat1 * Math.PI) / 180) *
    Math.cos((lat2 * Math.PI) / 180) *
    Math.sin(dLon / 2) ** 2;
  const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
  return R * c;
}

function Dashboard() {
  const [location, setLocation] = useState(null);
  const [distance, setDistance] = useState(null);

  useEffect(() => {
    const fetchEmergency = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/emergency');
        if (res.data && res.data.latitude && res.data.longitude) {
          setLocation(res.data);
          const dist = haversineDistance(
            [responder.latitude, responder.longitude],
            [res.data.latitude, res.data.longitude]
          );
          setDistance(dist.toFixed(2));
        } else {
          setLocation(null);
          setDistance(null);
        }
      } catch (error) {
        console.error('Error fetching emergency:', error);
      }
    };

    fetchEmergency();
    const interval = setInterval(fetchEmergency, 5000);
    return () => clearInterval(interval);
  }, []);

  const linePositions =
    location && responder
      ? [
          [responder.latitude, responder.longitude],
          [location.latitude, location.longitude],
        ]
      : [];

  return (
    <div className="bg-white p-6 rounded-xl shadow-xl w-full max-w-xl text-center">
      <h2 className="text-xl font-bold mb-4 text-gray-700">Emergency Response Dashboard</h2>
      {location ? (
        <>
          <p className="mb-1 font-semibold text-red-500">Reported Location - Latitude: {location.latitude}, Longitude: {location.longitude}</p>
          <p className="mb-1 font-semibold text-gray-600">Responder's Location - Latitude: {responder.latitude}, Longitude: {responder.longitude}</p>
          <p className="mb-4 font-semibold text-blue-600">Distance to Emergency: {distance} km</p>
          <MapContainer
            center={[location.latitude, location.longitude]}
            zoom={16}
            style={{ height: '550px', width: '100%', borderRadius: '12px' }}
          >
            <TileLayer
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              attribution="&copy; OpenStreetMap contributors"
            />
            <Marker position={[responder.latitude, responder.longitude]}>
              <Popup>Responder</Popup>
            </Marker>
            <Marker position={[location.latitude, location.longitude]}>
              <Popup>Reported Location</Popup>
            </Marker>
            <Polyline
              positions={linePositions}
              pathOptions={{ color: 'red', dashArray: '6' }}
            />
          </MapContainer>

          <div className="mt-6 flex justify-center space-x-4">
            <button
              onClick={() => alert("Responder is en route to the user's location.")}
              className="px-4 py-2 bg-red-600 text-white rounded hover:bg-blu0e-700"
            >
              Responding to Emergency
            </button>
            <button
              onClick={async () => {
                try {
                  await axios.post('http://localhost:5000/api/emergency/reset');
                  setLocation(null);
                  setDistance(null);
                } catch (err) {
                  console.error('Failed to reset emergency:', err);
                }
              }}
              className="px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700"
            >
              ✅ Mark Emergency as Resolved
            </button>
          </div>
        </>
      ) : (
        <p className="text-gray-500 mb-4">⏳ No active emergencies at this time.</p>
      )}
    </div>
  );
}

export default Dashboard;
