import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { MapContainer, TileLayer, Marker, Popup, Polyline } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl:
    'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
  iconUrl:
    'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
  shadowUrl:
    'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
});


const responderCoords = [14.3137, 121.0809];

function haversine(lat1, lon1, lat2, lon2) {
  const toRad = x => (x * Math.PI) / 180;
  const R = 6371e3; // meters
  const φ1 = toRad(lat1);
  const φ2 = toRad(lat2);
  const Δφ = toRad(lat2 - lat1);
  const Δλ = toRad(lon2 - lon1);
  const a =
    Math.sin(Δφ / 2) * Math.sin(Δφ / 2) +
    Math.cos(φ1) * Math.cos(φ2) *
    Math.sin(Δλ / 2) * Math.sin(Δλ / 2);
  const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
  return R * c; // in meters
}

function Dashboard() {
  const [location, setLocation] = useState(null);

  useEffect(() => {
    const fetchLocation = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/location/latest');
        if (res.data && typeof res.data.latitude === 'number' && typeof res.data.longitude === 'number') {
          setLocation(res.data);
        }
      } catch (error) {
        console.error('Error fetching location:', error);
      }
    };
    fetchLocation();
    const interval = setInterval(fetchLocation, 5000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="bg-white p-6 rounded-xl shadow-xl w-full max-w-xl text-center">
      <h2 className="text-xl font-semibold mb-2">Live GPS Tracker</h2>
      {location ? (
        <>
          <p>📍 User Latitude: {location.latitude.toFixed(6)}, Longitude: {location.longitude.toFixed(6)}</p>
          <p>🛡️ Responder Latitude: {responderCoords[0]}, Longitude: {responderCoords[1]}</p>
          <p className="mb-2">
            📏 Distance: {haversine(location.latitude, location.longitude, responderCoords[0], responderCoords[1]).toFixed(2)} meters
          </p>
          <MapContainer
            center={[location.latitude, location.longitude]}
            zoom={17}
            style={{ height: '400px', width: '100%', margin: 'auto' }}
            className="mb-4"
          >
            <TileLayer
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              attribution="&copy; OpenStreetMap contributors"
            />
            <Marker position={[location.latitude, location.longitude]}>
              <Popup>User Location</Popup>
            </Marker>
            <Marker position={responderCoords}>
              <Popup>Responder</Popup>
            </Marker>
            <Polyline positions={[[location.latitude, location.longitude], responderCoords]} color="blue" dashArray="5, 10" />
          </MapContainer>
        </>
      ) : (
        <p>⏳ Waiting for GPS data...</p>
      )}
    </div>
  );
}

export default Dashboard;
