import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  MapContainer,
  TileLayer,
  Marker,
  Popup,
  Polyline,
} from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// Fix marker icon issue
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl:
    'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
  iconUrl:
    'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
  shadowUrl:
    'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
});

// Fixed responder location (e.g., PUP Bi√±an)
const responderLat = 14.3110;
const responderLng = 121.0794;

// Haversine Distance Calculation
function calculateDistance(lat1, lon1, lat2, lon2) {
  const toRad = (value) => (value * Math.PI) / 180;
  const R = 6371; // Earth radius in km
  const dLat = toRad(lat2 - lat1);
  const dLon = toRad(lon2 - lon1);
  const a =
    Math.sin(dLat / 2) ** 2 +
    Math.cos(toRad(lat1)) * Math.cos(toRad(lat2)) * Math.sin(dLon / 2) ** 2;
  const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
  return R * c; // in km
}

function Dashboard() {
  const [location, setLocation] = useState(null);
  const [distance, setDistance] = useState(null);
  const [pathHistory, setPathHistory] = useState([]); // store past coordinates

  useEffect(() => {
    const fetchLocation = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/location/latest');
        if (
          res.data &&
          typeof res.data.latitude === 'number' &&
          typeof res.data.longitude === 'number'
        ) {
          const newLoc = { latitude: res.data.latitude, longitude: res.data.longitude };
          setLocation(newLoc);

          // Update path history
          setPathHistory((prev) => [...prev, [newLoc.latitude, newLoc.longitude]]);

          // Compute distance
          const dist = calculateDistance(
            responderLat,
            responderLng,
            newLoc.latitude,
            newLoc.longitude
          );
          setDistance(dist);
        }
      } catch (error) {
        console.error('Error fetching location:', error);
      }
    };

    fetchLocation();
    const interval = setInterval(fetchLocation, 5000);
    return () => clearInterval(interval);
  }, []);

  // Distance threshold for coloring
  const isNear = distance !== null && distance < 1; // km

  return (
    <div className="bg-white p-4 rounded shadow">
      <h2 className="text-xl font-semibold mb-4">üì° Live GPS Tracker</h2>
      {location ? (
        <>
          <p>üìç Latitude: {location.latitude}, Longitude: {location.longitude}</p>
          <p>üìè Distance from Responder: {distance?.toFixed(2)} km</p>
          <MapContainer
            center={[location.latitude, location.longitude]}
            zoom={17}
            style={{ height: '400px', width: '100%', marginTop: '1rem' }}
          >
            <TileLayer
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              attribution="&copy; OpenStreetMap contributors"
            />

            {/* Responder Marker */}
            <Marker position={[responderLat, responderLng]}>
              <Popup>Responder Location</Popup>
            </Marker>

            {/* User Marker with Color Indicator */}
            <Marker
              position={[location.latitude, location.longitude]}
              icon={new L.Icon({
                iconUrl: isNear
                  ? 'https://maps.google.com/mapfiles/ms/icons/green-dot.png'
                  : 'https://maps.google.com/mapfiles/ms/icons/red-dot.png',
                iconSize: [32, 32],
                iconAnchor: [16, 32],
                popupAnchor: [0, -32],
              })}
            >
              <Popup>User Location</Popup>
            </Marker>

            {/* Path Line */}
            {pathHistory.length > 1 && (
              <Polyline positions={pathHistory} color="blue" />
            )}
          </MapContainer>
        </>
      ) : (
        <p>‚è≥ Waiting for valid GPS data...</p>
      )}
    </div>
  );
}

export default Dashboard;
